// src/App.jsx
import React, { useState, useEffect, useRef } from "react";
import {
  motion,
  AnimatePresence,
  useScroll,
  useTransform,
  useSpring,
} from "framer-motion";

// Import Contexts and Hooks
import { useTheme } from "./context/ThemeContext";

// Import Components
import Preloader from "./components/layout/Preloader";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";
import BackToTopButton from "./components/ui/BackToTopButton";
import HeroSection from "./components/sections/HeroSection";
import MenuSection from "./components/sections/MenuSection";
import CartIcon from "./components/cart/CartIcon";
import FlyingCartItems from "./components/cart/FlyingCartItems";
import CartModal from "./components/cart/CartModal";

// A new component for the beautiful, seamless animated background
const AnimatedBackground = () => {
  const [elements, setElements] = useState([]);

  useEffect(() => {
    const newElements = Array.from({ length: 30 }).map(() => ({
      id: Math.random(),
      x: `${Math.random() * 100}%`,
      y: `${Math.random() * 100}%`,
      size: Math.random() * 3 + 1,
      opacity: Math.random() * 0.5 + 0.2,
      duration: Math.random() * 10 + 10,
    }));
    setElements(elements);
  }, []);

  return (
    <div className="absolute inset-0 z-[-1] overflow-hidden">
      {elements.map((el) => (
        <motion.div
          key={el.id}
          className="absolute rounded-full bg-amber-500/20 dark:bg-amber-300/10"
          style={{
            left: el.x,
            top: el.y,
            width: el.size * 10,
            height: el.size * 10,
            opacity: el.opacity,
          }}
          animate={{ y: [el.y, `calc(${el.y} - 30px)`, el.y] }}
          transition={{
            duration: el.duration,
            repeat: Infinity,
            repeatType: "mirror",
            ease: "easeInOut",
          }}
        />
      ))}
      <div className="absolute top-[-20%] right-[-20%] w-1/2 h-1/2 rounded-full bg-amber-500/10 dark:bg-yellow-500/5 blur-[150px]"></div>
      <div className="absolute bottom-[-20%] left-[-20%] w-1/2 h-1/2 rounded-full bg-orange-500/10 dark:bg-amber-600/5 blur-[150px]"></div>
    </div>
  );
};

function App() {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);

  const mainRef = useRef(null);
  const { scrollYProgress } = useScroll({ container: mainRef });

  // Using spring for a super smooth parallax effect
  const smoothScroll = useSpring(scrollYProgress, {
    stiffness: 200,
    damping: 50,
  });
  const backgroundY = useTransform(smoothScroll, [0, 1], ["0%", "-30%"]);

  useEffect(() => {
    document.title = "کافه مهراس - تجربه‌ای به‌یادماندنی";
  }, []);

  if (!theme) return null;

  return (
    <>
      <AnimatePresence>
        {loading && <Preloader onComplete={() => setLoading(false)} />}
      </AnimatePresence>

      <AnimatePresence>
        {!loading && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <Header />
            {/* The main content is now the scrollable container */}
            <main
              ref={mainRef}
              className="relative h-screen overflow-y-auto overflow-x-hidden"
            >
              <motion.div style={{ y: backgroundY }}>
                <AnimatedBackground />
              </motion.div>

              {/* Gradient Mask for seamless transition */}
              <div
                className="absolute top-0 left-0 w-full h-full"
                style={{
                  maskImage:
                    "linear-gradient(to bottom, black 0%, black 80%, transparent 100%)",
                  WebkitMaskImage:
                    "linear-gradient(to bottom, black 0%, black 80%, transparent 100%)",
                }}
              >
                <HeroSection />
              </div>

              {/* MenuSection is now placed after the mask */}
              <div className="relative z-10 bg-[#FFFBF5] dark:bg-[#1A120B]">
                <MenuSection />
                <Footer />
              </div>
            </main>

            <BackToTopButton />
            <CartIcon />
            <FlyingCartItems />
            <CartModal />
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

export default App;
