// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import {
  motion,
  AnimatePresence,
  useMotionValue,
  useSpring,
  useTransform,
} from "framer-motion";
import { Sun, Moon, Sparkles } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// --- Sub-component: Animated SVG Logo ---
const AnimatedLogo = () => {
  const [isHovered, setIsHovered] = useState(false);
  return (
    <motion.div
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      initial={{ scale: 0.8, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ type: "spring", stiffness: 300, damping: 20, delay: 0.5 }}
      className="cursor-pointer relative z-20" // Ensure logo is clickable
    >
      <svg width="120" height="40" viewBox="0 0 120 40" className="w-24 h-10">
        <motion.path
          d="M 20 25 C 10 25, 10 15, 20 15 L 40 15 C 50 15, 50 25, 40 25 Z"
          fill="currentColor"
          className="text-stone-700 dark:text-stone-300"
          transition={{ duration: 0.3 }}
        />
        <motion.path
          d="M 15 15 Q 20 10, 25 15"
          stroke="currentColor"
          strokeWidth="1.5"
          fill="none"
          strokeLinecap="round"
          initial={{ pathLength: 0, opacity: 0 }}
          animate={{
            pathLength: isHovered ? 1 : 0.5,
            opacity: isHovered ? 1 : 0.7,
          }}
          transition={{ duration: 0.5, ease: "easeInOut" }}
        />
        <motion.path
          d="M 25 15 Q 30 10, 35 15"
          stroke="currentColor"
          strokeWidth="1.5"
          fill="none"
          strokeLinecap="round"
          initial={{ pathLength: 0, opacity: 0 }}
          animate={{
            pathLength: isHovered ? 1 : 0.6,
            opacity: isHovered ? 1 : 0.7,
          }}
          transition={{ duration: 0.5, ease: "easeInOut", delay: 0.1 }}
        />
        <text
          x="55"
          y="27"
          fontFamily="Great Vibes, cursive"
          fontSize="24"
          fill="currentColor"
          className="text-stone-800 dark:text-stone-200"
        >
          Mehras
        </text>
      </svg>
    </motion.div>
  );
};

// --- Sub-component: Live Clock ---
const LiveClock = () => {
  const [time, setTime] = useState(new Date());
  useEffect(() => {
    const timerId = setInterval(() => setTime(new Date()), 60000); // Update every minute
    return () => clearInterval(timerId);
  }, []);
  return (
    <motion.div
      initial={{ y: -10, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1 }}
      className="hidden lg:flex items-center gap-2 text-sm font-medium text-stone-600 dark:text-stone-400"
    >
      {time.toLocaleTimeString("fa-IR", { hour: "2-digit", minute: "2-digit" })}
    </motion.div>
  );
};

// --- Sub-component: Magnetic Navigation Menu ---
const NavMenu = () => {
  const [hoveredLink, setHoveredLink] = useState(null);
  const links = [
    { name: "منو", href: "#menu" },
    { name: "تماس با ما", href: "#main-footer" },
  ];
  return (
    <nav
      onMouseLeave={() => setHoveredLink(null)}
      className="hidden md:flex items-center p-1 bg-stone-200/30 dark:bg-stone-800/30 rounded-full"
    >
      {links.map((link, i) => (
        <motion.a
          key={link.name}
          href={link.href}
          onMouseEnter={() => setHoveredLink(i)}
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{
            type: "spring",
            stiffness: 300,
            damping: 20,
            delay: 0.8 + i * 0.1,
          }}
          className="relative px-5 py-2.5 text-sm font-semibold text-stone-800 dark:text-stone-200 z-10"
        >
          {link.name}
          {hoveredLink === i && (
            <motion.div
              layoutId="header-nav-spotlight"
              className="absolute inset-0 rounded-full bg-white/70 dark:bg-black/50"
              transition={{ type: "spring", stiffness: 400, damping: 30 }}
            />
          )}
        </motion.a>
      ))}
    </nav>
  );
};

// --- Sub-component: Action Buttons ---
const ActionButtons = () => {
  const { theme, setTheme } = useTheme();
  const toggleTheme = () =>
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  return (
    <div className="flex items-center gap-3">
      <motion.button
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1.2 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        className="relative p-2.5 rounded-full text-amber-500 bg-amber-500/10"
      >
        <Sparkles size={20} />
        <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-amber-400 ring-2 ring-white dark:ring-[#1A120B] animate-pulse" />
      </motion.button>
      <motion.button
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1.1 }}
        onClick={toggleTheme}
        className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 bg-stone-200/40 dark:bg-stone-800/40"
        aria-label="Toggle theme"
      >
        <AnimatePresence mode="wait">
          <motion.div
            key={theme}
            initial={{ opacity: 0, rotate: -90 }}
            animate={{ opacity: 1, rotate: 0 }}
            exit={{ opacity: 0, rotate: 90 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
          >
            {theme === "dark" ? <Sun size={20} /> : <Moon size={20} />}
          </motion.div>
        </AnimatePresence>
      </motion.button>
    </div>
  );
};

// --- Main Header Component ---
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <motion.header
      initial={{ y: -120, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 120, damping: 20, delay: 0.2 }}
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
        isScrolled ? "py-2" : "py-4"
      }`}
    >
      <motion.div
        className={`mx-auto w-[95%] max-w-7xl rounded-2xl border border-white/10 transition-all duration-300
          ${
            isScrolled
              ? "bg-white/60 dark:bg-black/30 backdrop-blur-xl shadow-2xl shadow-black/10"
              : "bg-transparent"
          }`}
      >
        {/* NEW: Robust 3-column grid layout */}
        <div className="grid grid-cols-3 items-center px-4 h-20">
          {/* Left Column */}
          <div className="justify-self-start">
            <LiveClock />
          </div>

          {/* Center Column (for navigation on large screens) */}
          <div className="justify-self-center hidden lg:block">
            <NavMenu />
          </div>

          {/* Right Column */}
          <div className="justify-self-end">
            <ActionButtons />
          </div>
        </div>
      </motion.div>

      {/* Centered Logo (always visible, but more prominent on small screens) */}
      <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
        <AnimatedLogo />
      </div>
    </motion.header>
  );
};

export default Header;
