// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Moon } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// A reusable NavLink component to keep the code clean and manage hover effects
const NavLink = ({ children, href }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <a
      href={href}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      className="relative px-5 py-2.5 text-sm sm:text-base font-semibold text-stone-700 dark:text-stone-200 transition-colors duration-300"
    >
      {/* The text content of the link */}
      <span className="relative z-10">{children}</span>

      {/* The animated background that appears on hover */}
      {isHovered && (
        <motion.div
          layoutId="nav-background" // This ID is crucial for the magic-motion effect
          className="absolute inset-0 rounded-full bg-white/60 dark:bg-black/30"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1, transition: { duration: 0.15 } }}
          exit={{ opacity: 0, transition: { duration: 0.15, delay: 0.1 } }}
        />
      )}
    </a>
  );
};

const Header = () => {
  const { theme, setTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);

  // Effect to detect scroll and change header background
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  return (
    <motion.header
      className={`fixed top-0 left-0 right-0 z-40 transition-all duration-500 ${
        isScrolled
          ? "bg-[#FFFBF5]/80 dark:bg-[#1A120B]/80 backdrop-blur-lg shadow-md shadow-black/5"
          : "bg-transparent"
      }`}
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 120, damping: 20, delay: 0.2 }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-24">
        {/* === LOGO BUG FIX START === */}
        {/* Wrapping the logo in a div with padding prevents the glow from being clipped */}
        <div className="p-2">
          <h1 className="logo-title">Cafe Mehras</h1>
        </div>
        {/* === LOGO BUG FIX END === */}

        {/* === NAVIGATION REDESIGN START === */}
        <nav className="hidden md:flex items-center p-1.5 bg-stone-200/40 dark:bg-stone-800/40 rounded-full border border-stone-300/30 dark:border-stone-700/40">
          <NavLink href="#menu">منو</NavLink>
          <NavLink href="#main-footer">تماس با ما</NavLink>
        </nav>
        {/* === NAVIGATION REDESIGN END === */}

        {/* Theme Toggle Button */}
        <button
          onClick={toggleTheme}
          className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 hover:bg-stone-200/70 dark:hover:bg-stone-700/70 transition-all duration-300 hover:scale-110"
          aria-label="Toggle theme"
        >
          <AnimatePresence mode="wait" initial={false}>
            <motion.div
              key={theme}
              initial={{ y: -20, opacity: 0, rotate: -30 }}
              animate={{ y: 0, opacity: 1, rotate: 0 }}
              exit={{ y: 20, opacity: 0, rotate: 30 }}
              transition={{ duration: 0.2 }}
            >
              {theme === "dark" ? <Sun size={22} /> : <Moon size={22} />}
            </motion.div>
          </AnimatePresence>
        </button>
      </div>
    </motion.header>
  );
};

export default Header;
