// src/components/sections/HeroSection.jsx
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { ArrowDown, Zap, Coffee } from "lucide-react";
import FloatingIcon from "./FloatingIcon";

// A new sub-component for the beautiful, continuously floating coffee beans background
const AnimatedBackground = () => {
  const [beans, setBeans] = useState([]);

  useEffect(() => {
    // Generate random properties for each bean to create a natural, varied look
    const newBeans = Array.from({ length: 20 }).map(() => ({
      id: Math.random(),
      x: `${Math.random() * 100}%`,
      y: `${Math.random() * 100}%`,
      size: Math.random() * 10 + 5, // size between 5px and 15px
      opacity: Math.random() * 0.3 + 0.1, // opacity between 0.1 and 0.4
      duration: Math.random() * 15 + 10, // duration between 10 and 25 seconds
    }));
    setBeans(newBeans);
  }, []);

  return (
    <div className="absolute inset-0 z-0 overflow-hidden">
      {beans.map((bean) => (
        <motion.div
          key={bean.id}
          className="absolute text-amber-800/50 dark:text-amber-300/30"
          style={{
            left: bean.x,
            top: bean.y,
            width: bean.size,
            height: bean.size,
            opacity: bean.opacity,
          }}
          // This creates a smooth, continuous, and looping animation
          animate={{
            y: [
              `${bean.y}`,
              `calc(${bean.y} + ${Math.random() * 40 - 20}px)`,
              `${bean.y}`,
            ],
            x: [
              `${bean.x}`,
              `calc(${bean.x} + ${Math.random() * 40 - 20}px)`,
              `${bean.x}`,
            ],
          }}
          transition={{
            duration: bean.duration,
            repeat: Infinity,
            repeatType: "mirror",
            ease: "easeInOut",
          }}
        >
          {/* A simple but elegant SVG for a coffee bean */}
          <svg
            viewBox="0 0 24 24"
            fill="currentColor"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M12 2C6.477 2 2 6.477 2 12s4.477 10 10 10 10-4.477 10-10S17.523 2 12 2zm0 18c-4.411 0-8-3.589-8-8s3.589-8 8-8 8 3.589 8 8-3.589 8-8 8zm4-9c-.552 0-1-.448-1-1s.448-1 1-1 1 .448 1 1-.448 1-1 1zm-4 0c-.552 0-1-.448-1-1s.448-1 1-1 1 .448 1 1-.448 1-1 1zm-4 0c-.552 0-1-.448-1-1s.448-1 1-1 1 .448 1 1-.448 1-1 1z" />
          </svg>
        </motion.div>
      ))}
    </div>
  );
};

const HeroSection = () => {
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100 },
    },
  };

  return (
    <section className="relative min-h-screen flex items-center justify-center text-center px-4 overflow-hidden bg-[#FFFBF5] dark:bg-[#1A120B]">
      <AnimatedBackground />

      {/* Seamless Fade-out Gradient at the bottom */}
      <div className="absolute bottom-0 left-0 w-full h-48 z-10 bg-gradient-to-t from-[#FFFBF5] to-transparent dark:from-[#1A120B]"></div>

      {/* Main Content Container */}
      <motion.div
        className="relative z-20"
        initial="hidden"
        animate="visible"
        variants={{
          visible: { transition: { staggerChildren: 0.2, delayChildren: 0.3 } },
        }}
      >
        <div className="relative">
          <FloatingIcon
            Icon={Coffee}
            pos={{ top: "50%", left: "-15%" }}
            delay={1.2}
          />
          <FloatingIcon
            Icon={Zap}
            pos={{ top: "50%", right: "-15%" }}
            delay={1.4}
          />

          <motion.h1
            variants={itemVariants}
            className="text-6xl sm:text-7xl md:text-8xl font-black bg-clip-text text-transparent bg-gradient-to-r from-amber-600 via-orange-400 to-amber-600 dark:from-yellow-400 dark:via-amber-300 dark:to-yellow-400"
            style={{
              animation: "gradient-animation 5s ease infinite",
              backgroundSize: "200% 200%",
            }}
          >
            کافه مهراس
          </motion.h1>

          <motion.p
            variants={itemVariants}
            className="mt-8 max-w-xl mx-auto text-base sm:text-lg md:text-xl text-stone-700 dark:text-stone-200 font-medium"
          >
            <span className="relative inline-block px-4 py-2">
              <span className="absolute inset-0 bg-gradient-to-r from-amber-500 to-orange-400 rounded-lg blur-xl opacity-40 dark:opacity-60"></span>
              <span className="relative">عشق و قهوه در هوای شهر</span>
            </span>
          </motion.p>
        </div>

        <motion.div variants={itemVariants}>
          <a
            href="#menu"
            className="group inline-flex items-center justify-center mt-10 px-6 py-3 sm:px-8 sm:py-4 font-bold text-amber-800 dark:text-yellow-300 bg-amber-400/30 dark:bg-yellow-400/10 border border-amber-500/50 dark:border-yellow-400/30 rounded-xl hover:bg-amber-400/50 dark:hover:bg-yellow-400/20 transition-all duration-300 transform hover:scale-105 shadow-[0_0_20px_rgba(245,158,11,0.2)] hover:shadow-[0_0_30px_rgba(245,158,11,0.4)] dark:shadow-[0_0_20px_rgba(250,204,21,0.1)] dark:hover:shadow-[0_0_30px_rgba(250,204,21,0.2)]"
          >
            مشاهده منو{" "}
            <ArrowDown className="w-5 h-5 mr-2 transition-transform duration-300 group-hover:translate-y-1" />
          </a>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default HeroSection;
