// src/components/layout/Footer.jsx
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { MapPin, Instagram, Phone, Copy, Check } from "lucide-react";
import { api } from "../../api/mockAPI";

const Footer = () => {
  const [contact, setContact] = useState({});
  const [copiedPhone, setCopiedPhone] = useState(false);

  useEffect(() => {
    api.fetchContactInfo().then((data) => setContact(data));
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    setCopiedPhone(true);
    setTimeout(() => setCopiedPhone(false), 2000);
  };

  return (
    <motion.footer
      id="main-footer"
      className="bg-[#fdf6e9] dark:bg-[#160f0a] text-stone-700 dark:text-stone-300 pt-24 pb-12 px-4 sm:px-6 lg:px-8"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
    >
      <div className="text-center mb-16">
        <h2
          className="text-5xl md:text-7xl font-black bg-clip-text text-transparent bg-gradient-to-r from-amber-600 via-orange-400 to-amber-600 dark:from-yellow-400 dark:via-amber-300 dark:to-yellow-400"
          style={{
            animation: "gradient-animation 5s ease infinite",
            backgroundSize: "200% 200%",
          }}
        >
          کافه مهراس
        </h2>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl mx-auto">
        {/* Address Card */}
        <div className="p-6 bg-white/30 dark:bg-black/20 backdrop-blur-lg rounded-2xl border border-white/20 dark:border-white/10">
          <div className="flex items-center mb-3">
            <MapPin className="w-6 h-6 text-amber-600 dark:text-amber-500" />
            <h3 className="mr-3 font-bold text-lg text-stone-800 dark:text-white">
              آدرس
            </h3>
          </div>
          <p>{contact.location}</p>
        </div>
        {/* Phone Card */}
        <div className="p-6 bg-white/30 dark:bg-black/20 backdrop-blur-lg rounded-2xl border border-white/20 dark:border-white/10">
          <div className="flex items-center mb-3">
            <Phone className="w-6 h-6 text-amber-600 dark:text-amber-500" />
            <h3 className="mr-3 font-bold text-lg text-stone-800 dark:text-white">
              تلفن
            </h3>
          </div>
          <div className="flex items-center">
            <span dir="ltr">{contact.phone}</span>
            <button
              onClick={() => handleCopy(contact.phone)}
              className="mr-2 p-1 rounded-full hover:bg-stone-200 dark:hover:bg-stone-700 transition-colors"
            >
              {copiedPhone ? (
                <Check size={16} className="text-green-500" />
              ) : (
                <Copy size={16} />
              )}
            </button>
          </div>
        </div>
        {/* Instagram Card */}
        <div className="p-6 bg-white/30 dark:bg-black/20 backdrop-blur-lg rounded-2xl border border-white/20 dark:border-white/10">
          <div className="flex items-center mb-3">
            <Instagram className="w-6 h-6 text-amber-600 dark:text-amber-500" />
            <h3 className="mr-3 font-bold text-lg text-stone-800 dark:text-white">
              اینستاگرام
            </h3>
          </div>
          <a
            href={
              contact.instagram
                ? `https://instagram.com/${contact.instagram.substring(1)}`
                : "#"
            }
            target="_blank"
            rel="noopener noreferrer"
            className="hover:text-amber-600 dark:hover:text-amber-400 transition-colors"
          >
            {contact.instagram}
          </a>
        </div>
      </div>
      <div className="text-center text-sm text-stone-500 dark:text-stone-400 mt-16">
        <p>© {new Date().getFullYear()} کافه مهراس. تمام حقوق محفوظ است.</p>
      </div>
    </motion.footer>
  );
};

export default Footer;
