// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import {
  motion,
  AnimatePresence,
  useMotionValue,
  useSpring,
  useTransform,
} from "framer-motion";
import { Sun, Moon, Sparkles } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// --- Sub-component: Live Clock ---
// A small detail that adds a lot of "premium" feel.
const LiveClock = () => {
  const [time, setTime] = useState(new Date());
  useEffect(() => {
    const timerId = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timerId);
  }, []);
  return (
    <div className="hidden lg:flex items-center gap-2 text-sm font-medium text-stone-600 dark:text-stone-400">
      <motion.div
        initial={{ y: -10, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1 }}
      >
        {time.toLocaleTimeString("fa-IR", {
          hour: "2-digit",
          minute: "2-digit",
        })}
      </motion.div>
    </div>
  );
};

// --- Sub-component: Magnetic Navigation Menu ---
// The core of the interactive navigation experience.
const NavMenu = () => {
  const [hoveredLink, setHoveredLink] = useState(null);
  const links = [
    { name: "منو", href: "#menu" },
    { name: "تماس با ما", href: "#main-footer" },
  ];

  return (
    <nav
      onMouseLeave={() => setHoveredLink(null)}
      className="hidden md:flex items-center p-1 bg-stone-200/30 dark:bg-stone-800/30 rounded-full"
    >
      {links.map((link, i) => (
        <motion.a
          key={link.name}
          href={link.href}
          onMouseEnter={() => setHoveredLink(i)}
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{
            type: "spring",
            stiffness: 300,
            damping: 20,
            delay: 0.8 + i * 0.1,
          }}
          className="relative px-5 py-2.5 text-sm font-semibold text-stone-800 dark:text-stone-200 z-10"
        >
          {link.name}
          {hoveredLink === i && (
            <motion.div
              layoutId="header-nav-spotlight"
              className="absolute inset-0 rounded-full bg-white/70 dark:bg-black/50"
              transition={{ type: "spring", stiffness: 400, damping: 30 }}
            />
          )}
        </motion.a>
      ))}
    </nav>
  );
};

// --- Sub-component: Action Buttons (Theme Toggle & Special Offer) ---
const ActionButtons = () => {
  const { theme, setTheme } = useTheme();
  const toggleTheme = () =>
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));

  return (
    <div className="flex items-center gap-3">
      <motion.button
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1.2 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        className="relative p-2.5 rounded-full text-amber-500 bg-amber-500/10"
      >
        <Sparkles size={20} />
        <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-amber-400 ring-2 ring-white dark:ring-[#1A120B] animate-pulse" />
      </motion.button>
      <motion.button
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: "spring", stiffness: 300, damping: 20, delay: 1.1 }}
        onClick={toggleTheme}
        className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 bg-stone-200/40 dark:bg-stone-800/40"
        aria-label="Toggle theme"
      >
        <AnimatePresence mode="wait">
          <motion.div
            key={theme}
            initial={{ opacity: 0, rotate: -90 }}
            animate={{ opacity: 1, rotate: 0 }}
            exit={{ opacity: 0, rotate: 90 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
          >
            {theme === "dark" ? <Sun size={20} /> : <Moon size={20} />}
          </motion.div>
        </AnimatePresence>
      </motion.button>
    </div>
  );
};

// --- Main Header Component ---
// This component orchestrates everything.
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  // Mouse position tracking for the spotlight effect
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);
  const springConfig = { damping: 40, stiffness: 200, mass: 1.5 };
  const smoothMouseX = useSpring(mouseX, springConfig);
  const smoothMouseY = useSpring(mouseY, springConfig);

  const spotlightOpacity = useTransform(
    smoothMouseY,
    [0, 150], // y-range
    [isScrolled ? 0.08 : 0, 0] // opacity-range (only visible when scrolled and hovered)
  );

  const handleMouseMove = (e) => {
    const { clientX, clientY, currentTarget } = e;
    const { left, top } = currentTarget.getBoundingClientRect();
    mouseX.set(clientX - left);
    mouseY.set(clientY - top);
  };

  // Scroll detection effect
  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const headerHeight = isScrolled ? (isHovered ? 104 : 88) : 104;

  return (
    <motion.header
      onMouseMove={handleMouseMove}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => {
        setIsHovered(false);
        mouseX.set(0);
        mouseY.set(0);
      }}
      animate={{ height: headerHeight }}
      transition={{ type: "spring", stiffness: 400, damping: 30 }}
      className="fixed top-0 left-0 right-0 z-50 overflow-hidden"
    >
      <motion.div
        className="absolute inset-0"
        style={{
          background: useTransform(
            [smoothMouseX, smoothMouseY],
            ([x, y]) =>
              `radial-gradient(400px at ${x}px ${y}px, rgba(255, 255, 255, ${
                isHovered ? 0.08 : 0
              }), transparent 80%)`
          ),
        }}
      />

      <motion.div
        animate={{ y: isScrolled ? 0 : -100 }}
        transition={{ type: "spring", stiffness: 400, damping: 30 }}
        className="absolute inset-0 bg-white/50 dark:bg-black/20 backdrop-blur-2xl border-b border-white/10"
      />

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-full">
        <LiveClock />
        <motion.div
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{
            type: "spring",
            stiffness: 300,
            damping: 20,
            delay: 0.5,
          }}
        >
          <h1 className="logo-title">Cafe Mehras</h1>
        </motion.div>
        <div className="hidden lg:block">
          <NavMenu />
        </div>
        <div className="lg:hidden">
          {" "}
          {/* Placeholder for mobile menu button */}{" "}
        </div>
        <ActionButtons />
      </div>
    </motion.header>
  );
};

export default Header;
