// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Moon } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// --- Sub-component: NEW English Animated Text Logo ---
// This version uses the elegant 'Great Vibes' font for the English text.
const AnimatedTextLogo = () => {
  return (
    <motion.a
      href="#" // Link to home page
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.2 }}
      className="relative text-4xl cursor-pointer group" // Increased font size for better presence
      style={{ fontFamily: "'Great Vibes', cursive" }} // Using the elegant English font
    >
      <span className="bg-clip-text text-transparent bg-gradient-to-r from-amber-400 via-yellow-300 to-amber-500">
        Cafe Mehras
      </span>
      {/* The glowing shimmer effect on hover */}
      <span
        className="absolute inset-0 bg-clip-text text-transparent bg-gradient-to-r from-amber-400 via-yellow-300 to-amber-500 
                   opacity-0 group-hover:opacity-100 transition-opacity duration-500"
        style={{
          maskImage:
            "linear-gradient(to right, transparent, white, transparent)",
          animation: "shimmer 4s infinite",
        }}
      >
        Cafe Mehras
      </span>
    </motion.a>
  );
};

// --- Main Header Component ---
const Header = () => {
  const { theme, setTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredLink, setHoveredLink] = useState(null);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 20);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleTheme = () =>
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));

  const navLinks = [
    { name: "منو", href: "#menu" },
    { name: "تماس با ما", href: "#main-footer" },
  ];

  return (
    <motion.header
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 150, damping: 25, delay: 0.2 }}
      className="fixed top-0 left-0 right-0 z-50"
    >
      <div
        className={`container mx-auto px-4 sm:px-6 lg:px-8 transition-all duration-300 ease-in-out ${
          isScrolled ? "py-2" : "py-4"
        }`}
      >
        <div
          className={`relative flex items-center justify-between h-20 px-6 rounded-2xl transition-all duration-300 ease-in-out ${
            isScrolled
              ? "bg-white/60 dark:bg-black/30 backdrop-blur-xl shadow-lg shadow-black/5 border border-white/10"
              : ""
          }`}
        >
          {/* --- LEFT SIDE: LOGO --- */}
          <div className="flex-shrink-0">
            <AnimatedTextLogo />
          </div>

          {/* --- RIGHT SIDE: NAV & ACTIONS --- */}
          <div className="flex items-center gap-4">
            {/* Navigation Menu */}
            <nav
              className="hidden md:flex items-center p-1.5 bg-stone-200/50 dark:bg-stone-800/50 rounded-full"
              onMouseLeave={() => setHoveredLink(null)}
            >
              {navLinks.map((link) => (
                <a
                  key={link.name}
                  href={link.href}
                  onMouseEnter={() => setHoveredLink(link.name)}
                  className="relative px-6 py-2 text-sm font-semibold text-stone-700 dark:text-stone-200 transition-colors duration-300"
                >
                  <span className="relative z-10">{link.name}</span>
                  {hoveredLink === link.name && (
                    <motion.div
                      layoutId="header-nav-pill"
                      className="absolute inset-0 rounded-full bg-white/80 dark:bg-black/50"
                      transition={{
                        type: "spring",
                        stiffness: 400,
                        damping: 35,
                      }}
                    />
                  )}
                </a>
              ))}
            </nav>

            {/* Theme Toggle Button */}
            <motion.button
              onClick={toggleTheme}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 hover:bg-stone-200/70 dark:hover:bg-stone-700/70 transition-all duration-300"
              aria-label="Toggle theme"
            >
              <AnimatePresence mode="wait">
                <motion.div
                  key={theme}
                  initial={{ opacity: 0, rotate: -90 }}
                  animate={{ opacity: 1, rotate: 0 }}
                  exit={{ opacity: 0, rotate: 90 }}
                  transition={{ duration: 0.3, ease: "easeInOut" }}
                >
                  {theme === "dark" ? <Sun size={22} /> : <Moon size={22} />}
                </motion.div>
              </AnimatePresence>
            </motion.button>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
