// src/components/sections/HeroSection.jsx
import React, { useRef } from "react";
import { motion, useMotionValue, useTransform, useSpring } from "framer-motion";
import { ArrowDown, Zap, Coffee } from "lucide-react";
import FloatingIcon from "./FloatingIcon";

const HeroSection = () => {
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100 },
    },
  };

  const heroRef = useRef(null);

  // --- NEW: Mouse-driven Parallax Logic ---
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  // Use spring for a smooth, natural motion
  const springConfig = { damping: 30, stiffness: 100, mass: 0.5 };
  const smoothMouseX = useSpring(mouseX, springConfig);
  const smoothMouseY = useSpring(mouseY, springConfig);

  // Transform mouse position into a small range of movement for the parallax effect
  // Background moves in the opposite direction of the mouse
  const backgroundX = useTransform(
    smoothMouseX,
    [-0.5, 0.5],
    ["15px", "-15px"]
  );
  const backgroundY = useTransform(
    smoothMouseY,
    [-0.5, 0.5],
    ["15px", "-15px"]
  );

  // Foreground content moves with the mouse
  const contentX = useTransform(smoothMouseX, [-0.5, 0.5], ["-10px", "10px"]);
  const contentY = useTransform(smoothMouseY, [-0.5, 0.5], ["-10px", "10px"]);

  const handleMouseMove = (e) => {
    if (!heroRef.current) return;
    const { clientX, clientY } = e;
    const { left, top, width, height } =
      heroRef.current.getBoundingClientRect();
    // Normalize mouse position to a range of -0.5 to 0.5
    const normalizedX = (clientX - left) / width - 0.5;
    const normalizedY = (clientY - top) / height - 0.5;
    mouseX.set(normalizedX);
    mouseY.set(normalizedY);
  };

  const handleMouseLeave = () => {
    mouseX.set(0);
    mouseY.set(0);
  };

  return (
    <section
      ref={heroRef}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      className="relative min-h-screen flex items-center justify-center text-center px-4 overflow-hidden bg-[#1A120B]"
    >
      {/* --- NEW: Interactive Background Image Layer --- */}
      <motion.div
        className="absolute inset-[-30px] z-0" // Slightly larger to prevent empty edges during movement
        style={{
          backgroundImage:
            "url(https://images.unsplash.com/photo-1559925393-8be0ec4767c8?q=80&w=2070&auto=format&fit=crop)",
          backgroundSize: "cover",
          backgroundPosition: "center",
          x: backgroundX,
          y: backgroundY,
        }}
      />
      {/* A dark overlay to ensure text is always readable */}
      <div className="absolute inset-0 bg-black/60 z-0"></div>

      {/* --- NEW: Seamless Fade-out Gradient --- */}
      <div className="absolute bottom-0 left-0 w-full h-48 z-20 bg-gradient-to-t from-[#FFFBF5] to-transparent dark:from-[#1A120B]"></div>

      {/* --- NEW: Interactive Foreground Content Layer --- */}
      <motion.div
        className="relative z-10"
        style={{ x: contentX, y: contentY }}
      >
        <motion.div
          initial="hidden"
          animate="visible"
          variants={{
            visible: {
              transition: { staggerChildren: 0.2, delayChildren: 0.3 },
            },
          }}
        >
          <div className="relative">
            <FloatingIcon
              Icon={Coffee}
              pos={{ top: "50%", left: "-15%" }}
              delay={1.2}
            />
            <FloatingIcon
              Icon={Zap}
              pos={{ top: "50%", right: "-15%" }}
              delay={1.4}
            />

            <motion.h1
              variants={itemVariants}
              className="text-6xl sm:text-7xl md:text-8xl font-black bg-clip-text text-transparent bg-gradient-to-r from-amber-600 via-orange-400 to-amber-600 dark:from-yellow-400 dark:via-amber-300 dark:to-yellow-400"
              style={{
                animation: "gradient-animation 5s ease infinite",
                backgroundSize: "200% 200%",
              }}
            >
              کافه مهراس
            </motion.h1>

            <motion.p
              variants={itemVariants}
              className="mt-8 max-w-xl mx-auto text-base sm:text-lg md:text-xl text-stone-200 font-medium"
            >
              <span className="relative inline-block px-4 py-2">
                <span className="absolute inset-0 bg-gradient-to-r from-amber-500 to-orange-400 rounded-lg blur-xl opacity-40 dark:opacity-60"></span>
                <span className="relative">عشق و قهوه در هوای شهر</span>
              </span>
            </motion.p>
          </div>

          <motion.div variants={itemVariants}>
            <a
              href="#menu"
              className="group inline-flex items-center justify-center mt-10 px-6 py-3 sm:px-8 sm:py-4 font-bold text-yellow-300 bg-yellow-400/10 border border-yellow-400/30 rounded-xl hover:bg-yellow-400/20 transition-all duration-300 transform hover:scale-105 shadow-[0_0_20px_rgba(250,204,21,0.1)] hover:shadow-[0_0_30px_rgba(250,204,21,0.2)]"
            >
              مشاهده منو{" "}
              <ArrowDown className="w-5 h-5 mr-2 transition-transform duration-300 group-hover:translate-y-1" />
            </a>
          </motion.div>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default HeroSection;
