// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Moon } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

const Header = () => {
  const { theme, setTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);
  // State to track which navigation link is currently hovered
  const [hoveredLink, setHoveredLink] = useState(null);

  // Effect to detect scroll and change header background
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  const navLinks = [
    { name: "منو", href: "#menu" },
    { name: "تماس با ما", href: "#main-footer" },
  ];

  return (
    <motion.header
      className={`fixed top-0 left-0 right-0 z-40 transition-all duration-300 ${
        isScrolled
          ? "bg-[#FFFBF5]/80 dark:bg-[#1A120B]/80 backdrop-blur-lg shadow-sm"
          : "bg-transparent"
      }`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: "spring", stiffness: 300, damping: 30 }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-24">
        {/* Logo with padding to prevent glow clipping */}
        <div className="p-2">
          <h1 className="logo-title">Cafe Mehras</h1>
        </div>

        {/* --- Elegant Navigation Menu --- */}
        <nav
          className="hidden md:flex items-center p-1.5 bg-stone-200/50 dark:bg-stone-800/50 rounded-full"
          onMouseLeave={() => setHoveredLink(null)} // Reset hover when mouse leaves the nav area
        >
          {navLinks.map((link) => (
            <a
              key={link.name}
              href={link.href}
              onMouseEnter={() => setHoveredLink(link.name)} // Set the currently hovered link
              className="relative px-6 py-2 text-sm font-semibold text-stone-700 dark:text-stone-200 transition-colors duration-300"
            >
              <span className="relative z-10">{link.name}</span>
              {/* The magic motion background */}
              {hoveredLink === link.name && (
                <motion.div
                  layoutId="header-nav-pill" // Shared layoutId for the smooth animation
                  className="absolute inset-0 rounded-full bg-white dark:bg-black/50"
                  transition={{ type: "spring", stiffness: 400, damping: 35 }}
                />
              )}
            </a>
          ))}
        </nav>

        {/* --- Theme Toggle Button --- */}
        <button
          onClick={toggleTheme}
          className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 hover:bg-stone-200/70 dark:hover:bg-stone-700/70 transition-all duration-300 hover:scale-110 active:scale-95"
          aria-label="Toggle theme"
        >
          <AnimatePresence mode="wait" initial={false}>
            <motion.div
              key={theme}
              initial={{ y: -20, opacity: 0, rotate: -30 }}
              animate={{ y: 0, opacity: 1, rotate: 0 }}
              exit={{ y: 20, opacity: 0, rotate: 30 }}
              transition={{ duration: 0.2 }}
            >
              {theme === "dark" ? <Sun size={22} /> : <Moon size={22} />}
            </motion.div>
          </AnimatePresence>
        </button>
      </div>
    </motion.header>
  );
};

export default Header;
