// src/components/sections/HeroSection.jsx
import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ArrowDown, Zap, Coffee } from "lucide-react";
import FloatingIcon from "./FloatingIcon";

// A new sub-component for the beautiful, subtle floating particles background
const AnimatedBackground = () => {
  const particles = Array.from({ length: 25 }); // Create 25 particles

  return (
    <div className="absolute inset-0 z-0 overflow-hidden">
      {particles.map((_, i) => {
        const size = Math.random() * 5 + 2;
        const duration = Math.random() * 10 + 10;
        const delay = Math.random() * 5;
        return (
          <motion.div
            key={i}
            className="absolute rounded-full bg-amber-500/20 dark:bg-amber-300/10"
            style={{
              width: size,
              height: size,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: ["0%", "20%", "0%", "-20%", "0%"],
              x: ["0%", "-10%", "0%", "10%", "0%"],
            }}
            transition={{
              duration: duration,
              delay: delay,
              repeat: Infinity,
              repeatType: "mirror",
              ease: "easeInOut",
            }}
          />
        );
      })}
    </div>
  );
};

// A sub-component for the touch/click ripple effect
const Ripple = ({ x, y, onComplete }) => (
  <motion.div
    className="absolute rounded-full"
    style={{
      left: x,
      top: y,
      transform: "translate(-50%, -50%)",
      background:
        "radial-gradient(circle, rgba(255, 255, 255, 0.4) 0%, rgba(255, 255, 255, 0) 70%)",
    }}
    initial={{ width: 0, height: 0, opacity: 1 }}
    animate={{ width: 300, height: 300, opacity: 0 }}
    transition={{ duration: 0.7, ease: "easeOut" }}
    onAnimationComplete={onComplete}
  />
);

const HeroSection = () => {
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100 },
    },
  };

  // State to manage the ripples for the touch effect
  const [ripples, setRipples] = useState([]);

  const handleClick = (e) => {
    const { clientX, clientY, currentTarget } = e;
    const { left, top } = currentTarget.getBoundingClientRect();
    const newRipple = {
      id: Date.now(),
      x: clientX - left,
      y: clientY - top,
    };
    setRipples((current) => [...current, newRipple]);
  };

  const handleRippleComplete = (id) => {
    setRipples((current) => current.filter((r) => r.id !== id));
  };

  return (
    <section
      onClick={handleClick}
      className="relative min-h-screen flex items-center justify-center text-center px-4 overflow-hidden bg-[#FFFBF5] dark:bg-[#1A120B] cursor-pointer"
    >
      <AnimatedBackground />

      {/* The ripples will be rendered here */}
      <AnimatePresence>
        {ripples.map((ripple) => (
          <Ripple
            key={ripple.id}
            x={ripple.x}
            y={ripple.y}
            onComplete={() => handleRippleComplete(ripple.id)}
          />
        ))}
      </AnimatePresence>

      {/* Seamless Fade-out Gradient at the bottom */}
      <div className="absolute bottom-0 left-0 w-full h-48 z-10 bg-gradient-to-t from-[#FFFBF5] to-transparent dark:from-[#1A120B]"></div>

      {/* Main Content Container */}
      <motion.div
        className="relative z-20"
        initial="hidden"
        animate="visible"
        variants={{
          visible: { transition: { staggerChildren: 0.2, delayChildren: 0.3 } },
        }}
      >
        <div className="relative">
          <FloatingIcon
            Icon={Coffee}
            pos={{ top: "50%", left: "-15%" }}
            delay={1.2}
          />
          <FloatingIcon
            Icon={Zap}
            pos={{ top: "50%", right: "-15%" }}
            delay={1.4}
          />

          <motion.h1
            variants={itemVariants}
            className="text-6xl sm:text-7xl md:text-8xl font-black bg-clip-text text-transparent bg-gradient-to-r from-amber-600 via-orange-400 to-amber-600 dark:from-yellow-400 dark:via-amber-300 dark:to-yellow-400"
            style={{
              animation: "gradient-animation 5s ease infinite",
              backgroundSize: "200% 200%",
            }}
          >
            کافه مهراس
          </motion.h1>

          <motion.p
            variants={itemVariants}
            className="mt-8 max-w-xl mx-auto text-base sm:text-lg md:text-xl text-stone-700 dark:text-stone-200 font-medium"
          >
            <span className="relative inline-block px-4 py-2">
              <span className="absolute inset-0 bg-gradient-to-r from-amber-500 to-orange-400 rounded-lg blur-xl opacity-40 dark:opacity-60"></span>
              <span className="relative">عشق و قهوه در هوای شهر</span>
            </span>
          </motion.p>
        </div>

        <motion.div variants={itemVariants}>
          <a
            href="#menu"
            className="group inline-flex items-center justify-center mt-10 px-6 py-3 sm:px-8 sm:py-4 font-bold text-amber-800 dark:text-yellow-300 bg-amber-400/30 dark:bg-yellow-400/10 border border-amber-500/50 dark:border-yellow-400/30 rounded-xl hover:bg-amber-400/50 dark:hover:bg-yellow-400/20 transition-all duration-300 transform hover:scale-105 shadow-[0_0_20px_rgba(245,158,11,0.2)] hover:shadow-[0_0_30px_rgba(245,158,11,0.4)] dark:shadow-[0_0_20px_rgba(250,204,21,0.1)] dark:hover:shadow-[0_0_30px_rgba(250,204,21,0.2)]"
          >
            مشاهده منو{" "}
            <ArrowDown className="w-5 h-5 mr-2 transition-transform duration-300 group-hover:translate-y-1" />
          </a>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default HeroSection;
