// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Moon } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// A highly interactive and reusable NavLink component inspired by Dribbble designs.
const NavLink = ({ children, href, index }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.a
      href={href}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      // Staggered entrance animation for each link
      initial={{ y: -20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{
        type: "spring",
        stiffness: 300,
        damping: 20,
        delay: 0.5 + index * 0.1,
      }}
      className="relative px-5 py-2.5 text-sm sm:text-base font-semibold text-stone-700 dark:text-stone-200 transition-colors duration-300"
    >
      <span className="relative z-10">{children}</span>
      {isHovered && (
        <motion.div
          layoutId="header-nav-background" // This shared ID creates the magic move effect
          className="absolute inset-0 rounded-full bg-white/70 dark:bg-black/40"
          transition={{ type: "spring", stiffness: 500, damping: 30 }}
        />
      )}
    </motion.a>
  );
};

const Header = () => {
  const { theme, setTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);

  // Effect to detect scroll and apply the glassmorphism effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  return (
    <motion.header
      // The header now has a more pronounced glassmorphism effect on scroll
      className={`fixed top-4 left-1/2 -translate-x-1/2 w-[95%] max-w-7xl z-50 transition-all duration-300
        ${
          isScrolled
            ? "rounded-2xl border border-white/10 bg-white/30 dark:bg-black/20 backdrop-blur-xl shadow-2xl shadow-black/10"
            : "bg-transparent"
        }`}
      initial={{ y: -120, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 120, damping: 20, delay: 0.2 }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
        {/* Logo with padding to prevent glow clipping */}
        <div className="p-2">
          <h1 className="logo-title">Cafe Mehras</h1>
        </div>

        {/* Dribbble-inspired pill navigation with a glass effect */}
        <nav className="hidden md:flex items-center p-1 bg-stone-200/20 dark:bg-stone-800/20 rounded-full border border-stone-300/20 dark:border-stone-700/20">
          <NavLink href="#menu" index={0}>
            منو
          </NavLink>
          <NavLink href="#main-footer" index={1}>
            تماس با ما
          </NavLink>
        </nav>

        {/* Upgraded Theme Toggle Button */}
        <motion.button
          onClick={toggleTheme}
          whileHover={{ scale: 1.1, rotate: 5 }}
          whileTap={{ scale: 0.9, rotate: -5 }}
          className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 bg-stone-200/20 dark:bg-stone-800/20 border border-stone-300/20 dark:border-stone-700/20"
          aria-label="Toggle theme"
        >
          <AnimatePresence mode="wait" initial={false}>
            <motion.div
              key={theme}
              initial={{ y: -20, opacity: 0, rotate: -45 }}
              animate={{ y: 0, opacity: 1, rotate: 0 }}
              exit={{ y: 20, opacity: 0, rotate: 45 }}
              transition={{
                type: "spring",
                stiffness: 500,
                damping: 30,
                duration: 0.2,
              }}
            >
              {theme === "dark" ? <Sun size={20} /> : <Moon size={20} />}
            </motion.div>
          </AnimatePresence>
        </motion.button>
      </div>
    </motion.header>
  );
};

export default Header;
