// src/components/sections/HeroSection.jsx
import React, { useRef, useEffect, useState } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import { ArrowDown, Zap, Coffee } from "lucide-react";
import FloatingIcon from "./FloatingIcon";

// A new sub-component to create the beautiful floating coffee beans background
const FloatingCoffeeBeans = () => {
  const [beans, setBeans] = useState([]);

  useEffect(() => {
    // Generate 20 random beans on component mount
    const newBeans = Array.from({ length: 20 }).map(() => ({
      id: Math.random(),
      x: `${Math.random() * 100}%`,
      y: `${Math.random() * 100}%`,
      size: Math.random() * 8 + 4, // size between 4 and 12
      duration: Math.random() * 5 + 5, // duration between 5 and 10 seconds
    }));
    setBeans(newBeans);
  }, []);

  return (
    <div className="absolute inset-0 z-0 opacity-40 dark:opacity-20">
      {beans.map((bean) => (
        <motion.div
          key={bean.id}
          className="absolute text-amber-800/50 dark:text-amber-300/30"
          style={{
            left: bean.x,
            top: bean.y,
            width: bean.size,
            height: bean.size,
          }}
          animate={{ y: [bean.y, `calc(${bean.y} + 20px)`, bean.y] }}
          transition={{
            duration: bean.duration,
            repeat: Infinity,
            repeatType: "mirror",
            ease: "easeInOut",
          }}
        >
          {/* A simple SVG for a coffee bean */}
          <svg viewBox="0 0 24 24" fill="currentColor">
            <path d="M12,2A10,10,0,1,0,22,12,10,10,0,0,0,12,2Zm0,18a8,8,0,0,1-8-8,7.92,7.92,0,0,1,2.82-6.18,8,8,0,0,1,11.36,6.18A8,8,0,0,1,12,20Z" />
          </svg>
        </motion.div>
      ))}
    </div>
  );
};

const HeroSection = () => {
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { type: "spring", stiffness: 100 },
    },
  };

  // --- NEW: Scroll Animation Logic ---
  const heroRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"], // Track scroll from the start of the section to its end
  });

  // Create a parallax effect for the background
  const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "50%"]);
  // Create a fade-out effect for the background
  const backgroundOpacity = useTransform(
    scrollYProgress,
    [0, 0.8, 1],
    [1, 1, 0]
  );

  return (
    <section
      ref={heroRef}
      className="relative min-h-screen flex flex-col items-center justify-center text-center px-4 overflow-hidden"
    >
      {/* The main background color */}
      <div className="absolute inset-0 z-[-2] bg-[#FFFBF5] dark:bg-[#1A120B] transition-colors duration-500"></div>

      {/* The animated background container with parallax and fade effects */}
      <motion.div
        className="absolute inset-0 z-[-1]"
        style={{ y: backgroundY, opacity: backgroundOpacity }}
      >
        <FloatingCoffeeBeans />
        {/* Decorative Gradients */}
        <div className="absolute top-[-20%] right-[-20%] w-1/2 h-1/2 rounded-full bg-amber-500/10 dark:bg-yellow-500/5 blur-[120px]"></div>
        <div className="absolute bottom-[-20%] left-[-20%] w-1/2 h-1/2 rounded-full bg-orange-500/10 dark:bg-amber-600/5 blur-[120px]"></div>
      </motion.div>

      <motion.div
        initial="hidden"
        animate="visible"
        variants={{
          visible: { transition: { staggerChildren: 0.2, delayChildren: 0.3 } },
        }}
        className="relative z-10"
      >
        <div className="relative">
          <FloatingIcon
            Icon={Coffee}
            pos={{ top: "50%", left: "-15%" }}
            delay={1.2}
          />
          <FloatingIcon
            Icon={Zap}
            pos={{ top: "50%", right: "-15%" }}
            delay={1.4}
          />

          <motion.h1
            variants={itemVariants}
            className="text-6xl sm:text-7xl md:text-8xl font-black bg-clip-text text-transparent bg-gradient-to-r from-amber-600 via-orange-400 to-amber-600 dark:from-yellow-400 dark:via-amber-300 dark:to-yellow-400"
            style={{
              animation: "gradient-animation 5s ease infinite",
              backgroundSize: "200% 200%",
            }}
          >
            کافه مهراس
          </motion.h1>

          <motion.p
            variants={itemVariants}
            className="mt-8 max-w-xl mx-auto text-base sm:text-lg md:text-xl text-stone-700 dark:text-stone-200 font-medium"
          >
            <span className="relative inline-block px-4 py-2">
              <span className="absolute inset-0 bg-gradient-to-r from-amber-500 to-orange-400 rounded-lg blur-xl opacity-40 dark:opacity-60"></span>
              <span className="relative">عشق و قهوه در هوای شهر</span>
            </span>
          </motion.p>
        </div>

        <motion.div variants={itemVariants}>
          <a
            href="#menu"
            className="group inline-flex items-center justify-center mt-10 px-6 py-3 sm:px-8 sm:py-4 font-bold text-amber-800 dark:text-yellow-300 bg-amber-400/30 dark:bg-yellow-400/10 border border-amber-500/50 dark:border-yellow-400/30 rounded-xl hover:bg-amber-400/50 dark:hover:bg-yellow-400/20 transition-all duration-300 transform hover:scale-105 shadow-[0_0_20px_rgba(245,158,11,0.2)] hover:shadow-[0_0_30px_rgba(245,158,11,0.4)] dark:shadow-[0_0_20px_rgba(250,204,21,0.1)] dark:hover:shadow-[0_0_30px_rgba(250,204,21,0.2)]"
          >
            مشاهده منو{" "}
            <ArrowDown className="w-5 h-5 mr-2 transition-transform duration-300 group-hover:translate-y-1" />
          </a>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default HeroSection;
