// src/components/layout/Footer.jsx
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import {
  MapPin,
  Phone,
  Instagram,
  Send,
  Copy,
  Check,
  Coffee,
} from "lucide-react";
import { api } from "../../api/mockAPI";

// A reusable component for social media links with a nice hover effect
const SocialLink = ({ href, icon: Icon }) => (
  <a
    href={href}
    target="_blank"
    rel="noopener noreferrer"
    className="p-3 bg-stone-200/50 dark:bg-stone-800/50 rounded-full text-stone-700 dark:text-stone-300
               transition-all duration-300 hover:bg-amber-500 hover:text-white hover:scale-110"
  >
    <Icon size={20} />
  </a>
);

const Footer = () => {
  const [contact, setContact] = useState({});
  const [copiedPhone, setCopiedPhone] = useState(false);

  useEffect(() => {
    api.fetchContactInfo().then((data) => setContact(data));
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
    setCopiedPhone(true);
    setTimeout(() => setCopiedPhone(false), 2000);
  };

  return (
    <motion.footer
      id="main-footer"
      className="relative bg-[#fdf6e9] dark:bg-gradient-to-b dark:from-[#1c140f] dark:to-[#1A120B] text-stone-700 dark:text-stone-300 pt-24 pb-12 px-4 sm:px-6 lg:px-8 overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
    >
      {/* Subtle background noise texture */}
      <div
        className="absolute inset-0 z-0 opacity-20 dark:opacity-30"
        style={{
          backgroundImage:
            "url(https://www.transparenttextures.com/patterns/noise-lines.png)",
        }}
      ></div>

      <div className="relative z-10 container mx-auto">
        {/* Decorative Separator */}
        <div className="relative text-center mb-16">
          <hr className="border-stone-300 dark:border-stone-700" />
          <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 p-2 bg-[#fdf6e9] dark:bg-[#1A120B]">
            <Coffee className="text-amber-600 dark:text-amber-500" />
          </div>
        </div>

        {/* Main Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
          {/* --- LEFT COLUMN: MAP --- */}
          <motion.div
            className="w-full h-80 rounded-2xl overflow-hidden shadow-2xl group cursor-pointer"
            variants={{
              hidden: { opacity: 0, x: -50 },
              visible: {
                opacity: 1,
                x: 0,
                transition: { duration: 0.7, ease: "easeOut" },
              },
            }}
          >
            <img
              src="https://placehold.co/800x600/3d342e/ffffff?text=Our+Location"
              alt="[تصویری از نقشه موقعیت کافه]"
              className="w-full h-full object-cover transition-transform duration-500 ease-in-out group-hover:scale-105"
            />
          </motion.div>

          {/* --- RIGHT COLUMN: INFO & SOCIALS --- */}
          <motion.div
            className="space-y-8"
            variants={{
              hidden: { opacity: 0, x: 50 },
              visible: {
                opacity: 1,
                x: 0,
                transition: { duration: 0.7, ease: "easeOut" },
              },
            }}
          >
            <h2 className="text-4xl md:text-5xl font-bold text-stone-800 dark:text-white">
              به ما سر بزنید
            </h2>
            <p className="text-lg text-stone-600 dark:text-stone-400 leading-relaxed">
              منتظر حضور گرم شما در فضای دنج و صمیمی کافه مهراس هستیم تا بهترین
              لحظات را با طعم قهوه برایتان رقم بزنیم.
            </p>

            {/* Contact Info */}
            <div className="space-y-4">
              <div className="flex items-start gap-4">
                <MapPin className="w-6 h-6 text-amber-600 dark:text-amber-500 flex-shrink-0 mt-1" />
                <span className="text-base">{contact.location}</span>
              </div>
              <div className="flex items-center gap-4">
                <Phone className="w-6 h-6 text-amber-600 dark:text-amber-500 flex-shrink-0" />
                <div className="flex items-center">
                  <span dir="ltr" className="text-base">
                    {contact.phone}
                  </span>
                  <button
                    onClick={() => handleCopy(contact.phone)}
                    className="ml-2 p-1.5 rounded-full hover:bg-stone-200 dark:hover:bg-stone-700 transition-colors"
                  >
                    {copiedPhone ? (
                      <Check size={16} className="text-green-500" />
                    ) : (
                      <Copy size={16} />
                    )}
                  </button>
                </div>
              </div>
            </div>

            {/* Social Media Links */}
            <div className="flex items-center gap-4 pt-4">
              <SocialLink
                href={`https://instagram.com/${contact.instagram?.substring(
                  1
                )}`}
                icon={Instagram}
              />
              <SocialLink
                href={`https://t.me/${contact.telegram?.substring(1)}`}
                icon={Send}
              />
            </div>
          </motion.div>
        </div>

        {/* Copyright */}
        <div className="text-center text-sm text-stone-500 dark:text-stone-400 mt-24 border-t border-stone-300 dark:border-stone-700 pt-8">
          <p>© {new Date().getFullYear()} کافه مهراس. تمام حقوق محفوظ است.</p>
        </div>
      </div>
    </motion.footer>
  );
};

export default Footer;
