// src/styles/GlobalStyles.jsx
import React from "react";

const GlobalStyles = () => (
  <style>{`
        /* Importing Google Fonts */
        @import url('https://fonts.googleapis.com/css2?family=Vazirmatn:wght@300;400;500;700;900&display=swap');
        @import url('https://fonts.googleapis.com/css2?family=Great+Vibes&display=swap');

        /* Basic HTML and Body styles */
        html {
            scroll-behavior: smooth;
        }
        body {
            font-family: 'Vazirmatn', sans-serif;
            direction: rtl;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
            background-color: #FFFBF5; /* Light mode background */
            color: #44403c; /* Light mode text */
            transition: background-color 0.5s, color 0.5s;
        }
        body.dark {
            background-color: #1A120B; /* Dark mode background */
            color: #d6d3d1; /* Dark mode text */
        }

        /* Custom scrollbar utility */
        .no-scrollbar::-webkit-scrollbar {
            display: none;
        }
        .no-scrollbar {
            -ms-overflow-style: none;  /* IE and Edge */
            scrollbar-width: none;  /* Firefox */
        }
        
        /* Global Keyframe Animations */
        @keyframes gradient-animation {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
        }
        @keyframes shimmer {
            0% { background-position: -1000px 0; }
            100% { background-position: 1000px 0; }
        }
        .shimmer-bg {
            background: linear-gradient(to right, transparent 0%, rgba(255,255,255,0.1) 50%, transparent 100%);
            animation: shimmer 2s infinite;
            background-size: 2000px 100%;
        }

        /* Animated Logo Styles */
        .logo-title {
            font-family: 'Great Vibes', cursive;
            font-weight: 400;
            font-size: 2.25rem; /* 36px */
            line-height: 1;
            background: linear-gradient(90deg, #c0a062, #f8f0a8, #ffd700, #f8f0a8, #c0a062);
            background-size: 300% auto;
            color: transparent;
            background-clip: text;
            -webkit-background-clip: text;
            animation: 
                gradient-flow 10s linear infinite,
                float 6s ease-in-out infinite,
                glow 2.5s ease-in-out infinite alternate;
        }

        @keyframes gradient-flow {
            from { background-position: 0% center; }
            to { background-position: 300% center; }
        }
        
        @keyframes float {
            0% { transform: translateY(0); }
            50% { transform: translateY(-3px); }
            100% { transform: translateY(0); }
        }

        @keyframes glow {
            from { filter: drop-shadow(0 0 2px rgba(255, 215, 0, 0.5)); }
            to { filter: drop-shadow(0 0 8px rgba(255, 215, 0, 0.8)); }
        }
    `}</style>
);

export default GlobalStyles;
