// src/App.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Import Contexts and Hooks
import { useTheme } from "./context/ThemeContext";

// Import Layout Components
import Preloader from "./components/layout/Preloader";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";

// Import UI Components
import BackToTopButton from "./components/ui/BackToTopButton";

// Import Section Components
import HeroSection from "./components/sections/HeroSection";
import MenuSection from "./components/sections/MenuSection";

// Import Cart Components
import CartIcon from "./components/cart/CartIcon";
import FlyingCartItems from "./components/cart/FlyingCartItems";
import CartModal from "./components/cart/CartModal";

function App() {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);

  // Set document title once
  useEffect(() => {
    document.title = "کافه مهراس - تجربه‌ای به‌یادماندنی";
  }, []);

  // Prevent rendering until the theme is loaded to avoid flashes of unstyled content
  if (!theme) {
    return null; // Or a minimal loader
  }

  return (
    <>
      {/* Show preloader until loading is false */}
      <AnimatePresence>
        {loading && <Preloader onComplete={() => setLoading(false)} />}
      </AnimatePresence>

      {/* Show the main content after loading */}
      <AnimatePresence>
        {!loading && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <Header />
            <main>
              <HeroSection />
              <MenuSection />
            </main>
            <Footer />

            {/* UI Components that are fixed on the page */}
            <BackToTopButton />
            <CartIcon />
            <FlyingCartItems />
            <CartModal />
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

export default App;
