// src/components/layout/Header.jsx
import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Sun, Moon } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

// --- Sub-component: Animated SVG Logo ---
const AnimatedLogo = () => {
  const [isHovered, setIsHovered] = useState(false);
  return (
    <motion.a
      href="#" // Link to home page
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.2 }}
      className="cursor-pointer relative z-20"
    >
      <svg width="120" height="40" viewBox="0 0 120 40" className="w-24 h-10">
        <motion.path
          d="M 20 25 C 10 25, 10 15, 20 15 L 40 15 C 50 15, 50 25, 40 25 Z"
          fill="currentColor"
          className="text-stone-700 dark:text-stone-300"
        />
        <motion.path
          d="M 15 15 Q 20 10, 25 15"
          stroke="currentColor"
          strokeWidth="1.5"
          fill="none"
          strokeLinecap="round"
          animate={{
            pathLength: isHovered ? 1 : 0.5,
            opacity: isHovered ? 1 : 0.7,
          }}
          transition={{ duration: 0.5, ease: "easeInOut" }}
        />
        <motion.path
          d="M 25 15 Q 30 10, 35 15"
          stroke="currentColor"
          strokeWidth="1.5"
          fill="none"
          strokeLinecap="round"
          animate={{
            pathLength: isHovered ? 1 : 0.6,
            opacity: isHovered ? 1 : 0.7,
          }}
          transition={{ duration: 0.5, ease: "easeInOut", delay: 0.1 }}
        />
        <text
          x="55"
          y="27"
          fontFamily="Great Vibes, cursive"
          fontSize="24"
          fill="currentColor"
          className="text-stone-800 dark:text-stone-200"
        >
          Mehras
        </text>
      </svg>
    </motion.a>
  );
};

// --- Main Header Component ---
const Header = () => {
  const { theme, setTheme } = useTheme();
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredLink, setHoveredLink] = useState(null);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 20);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const toggleTheme = () =>
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));

  const navLinks = [
    { name: "منو", href: "#menu" },
    { name: "تماس با ما", href: "#main-footer" },
  ];

  return (
    <motion.header
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ type: "spring", stiffness: 150, damping: 25, delay: 0.2 }}
      className="fixed top-0 left-0 right-0 z-50"
    >
      <div
        className={`container mx-auto px-4 sm:px-6 lg:px-8 transition-all duration-300 ease-in-out ${
          isScrolled ? "py-2" : "py-4"
        }`}
      >
        <div
          className={`relative flex items-center justify-between h-20 px-6 rounded-2xl transition-all duration-300 ease-in-out ${
            isScrolled
              ? "bg-white/60 dark:bg-black/30 backdrop-blur-xl shadow-lg shadow-black/5 border border-white/10"
              : ""
          }`}
        >
          {/* --- LEFT SIDE: LOGO --- */}
          <div className="flex-shrink-0">
            <AnimatedLogo />
          </div>

          {/* --- RIGHT SIDE: NAV & ACTIONS --- */}
          <div className="flex items-center gap-4">
            {/* Navigation Menu */}
            <nav
              className="hidden md:flex items-center p-1 bg-stone-200/50 dark:bg-stone-800/50 rounded-full"
              onMouseLeave={() => setHoveredLink(null)}
            >
              {navLinks.map((link) => (
                <a
                  key={link.name}
                  href={link.href}
                  onMouseEnter={() => setHoveredLink(link.name)}
                  className="relative px-6 py-2 text-sm font-semibold text-stone-700 dark:text-stone-200 transition-colors duration-300"
                >
                  <span className="relative z-10">{link.name}</span>
                  {hoveredLink === link.name && (
                    <motion.div
                      layoutId="header-nav-pill"
                      className="absolute inset-0 rounded-full bg-white/80 dark:bg-black/50"
                      transition={{
                        type: "spring",
                        stiffness: 400,
                        damping: 35,
                      }}
                    />
                  )}
                </a>
              ))}
            </nav>

            {/* Theme Toggle Button */}
            <motion.button
              onClick={toggleTheme}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className="p-2.5 rounded-full text-stone-700 dark:text-stone-300 hover:bg-stone-200/70 dark:hover:bg-stone-700/70 transition-all duration-300"
              aria-label="Toggle theme"
            >
              <AnimatePresence mode="wait">
                <motion.div
                  key={theme}
                  initial={{ opacity: 0, rotate: -90 }}
                  animate={{ opacity: 1, rotate: 0 }}
                  exit={{ opacity: 0, rotate: 90 }}
                  transition={{ duration: 0.3, ease: "easeInOut" }}
                >
                  {theme === "dark" ? <Sun size={22} /> : <Moon size={22} />}
                </motion.div>
              </AnimatePresence>
            </motion.button>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
